import React, {useState, useEffect} from 'react';
import { Button } from '@material-ui/core';
import countapi from 'countapi-js';

export default function CounterButton() {
	const [currentCount, setCurrentCount] = useState(0);

	useEffect(() => {
		countapi.hit('1ccb732e-b55a-4404-ad3f-0f99c02fe44e').then(result => setCurrentCount(result.value));
	}, []);

	return (
		<Button 
          color="primary" 
          variant="outlined"
          onClick={() => {
            countapi.hit('1ccb732e-b55a-4404-ad3f-0f99c02fe44e').then(result => setCurrentCount(result.value));
          }}
        >
          The count on this button is {currentCount}
        </Button>
	);
}
